import { useState } from 'react';

import logo from './logo.svg';
import './App.css';

function App() {

  var quoteAuthor="Click on Quote Text";
  var quoteText="Clcik on New Quote";

  let apiQuotes = [];

  // Loading Spinner Shown
  function loading() {
    //loader.hidden = false;
    //quoteContainer.hidden = true;
  }

  // Remove Loading Spinner
  function complete() {
    //quoteContainer.hidden = false;
    //loader.hidden = true;
  }

  function newQuote() {
    //loading();
    const quote = apiQuotes[Math.floor(Math.random() * apiQuotes.length)];
    console.log(JSON.stringify(quote)+" "+JSON.stringify(quote.author)+" "+JSON.stringify(quote.text));
    if (!JSON.stringify(quote.author)) {
      quoteAuthor =  "Unknown";
    } else {
    quoteText = JSON.stringify(quote.author);
    }
    quoteText = JSON.stringify(quote.text);
    //complete();
  }

  async function getQuotes() {
    //loading();
    const apiURL = "https://type.fit/api/quotes";
    try {
      const response = await fetch(apiURL);
      apiQuotes = await response.json();
      console.log(apiQuotes[12]);
      newQuote();
    } catch (err) {console.log(err);}
  }

  return (
    <div>
      <div>
        <div className="quote-container" id="quote-container">
          {/* Quote */}
          <div className="quote-text">
            <i className="fas fa-quote-left" />
            {quoteText}
            <span id="quote" />
          </div>
          {/* Author */}
          <div className="quote-author">
            <span id="author" >{quoteAuthor}</span>
          </div>
          {/* Buttons */}
          <div className="button-container">
            <a
              className="twitter-button"
              id="twitter"
              title="Tweet This!"
             // href={`https://twitter.com/intent/tweet?text=${quoteText.textContent}-${authorText.textContent}`}
              target="_blank"
            >
              <i className="fab fa-twitter" />
            </a>
            <button id="new-quote" onClick={getQuotes()}
            >New Quote</button>
          </div>
        </div>
        {/* Loader */}
        <div className="loader" id="loader" />
      </div>
    </div>
  );
}

export default App;
