import { useState } from 'react';

import logo from './logo.svg';
import './App.css';

function App() {

  const [quoteAuthorString, setQuoteAuthorString] = useState("");

  var quoteAuthor = "";
  var quoteText = "";

  let apiQuotes = [];
 const getQuotes = async () => {
    const apiURL = "https://type.fit/api/quotes";
    try {
      const response = await fetch(apiURL);
      apiQuotes = await response.json();
      console.log(apiQuotes.length)
      const quote =
        apiQuotes[Math.floor(Math.random() * apiQuotes.length)].author;

      if (!JSON.stringify(quote.author)) {
        quoteAuthor = "Unknown";
      } else {
        quoteAuthor = JSON.stringify(quote.author);
        // setAuthorName(quote.author);
      }
      // quoteText = JSON.stringify(quote.text);
      quoteText="123456789"
      setQuoteAuthorString(JSON.stringify(quote.author));
      console.log(quoteText);
    } catch (err) {console.log(err);}
 }
  console.log(quoteText+" "+quoteAuthor);

  return (
    <div>
      <div>
        <div className="quote-container" id="quote-container">
          {/* Quote */}
          <div className="quote-text">
            <i className="fas fa-quote-left" />
            {quoteText}
            <span id="quote" />
          </div>
          {/* Author */}
          <div className="quote-author">
            <span id="author">{quoteAuthor}</span>
          </div>
          {/* Buttons */}
          <div className="button-container">
            <a
              className="twitter-button"
              id="twitter"
              title="Tweet This!"
              // href={`https://twitter.com/intent/tweet?text=${quoteText.textContent}-${authorText.textContent}`}
              target="_blank"
            >
              <i className="fab fa-twitter" />
            </a>
            <button id="new-quote" onClick={getQuotes}>
              New Quote
            </button>
          </div>
        </div>
        {/* Loader */}
        <div className="loader" id="loader" />
      </div>
    </div>
  );
}

export default App;
